CREATE OR REPLACE FUNCTION obter_conc_elem_nut_pac ( nr_sequencia_p bigint, ie_tipo_elemento_p text) RETURNS bigint AS $body$
DECLARE

pr_item_solucao_w	double precision;


BEGIN

/*
SQL base

select	nvl(sum(nvl(qt_volume,0)),0)
from	nut_pac_elem_mat a,
		nut_pac_elemento b,
		nut_pac c,
		nut_elemento d
where	a.nr_seq_pac_elem	= b.nr_sequencia
and		b.nr_seq_nut_pac  = c.nr_sequencia
and		b.nr_seq_elemento = d.nr_sequencia
and		c.nr_prescricao = :nr_prescricao
and		c.ie_npt_adulta	= 'N'
and		d.ie_tipo_elemento = :ie_tipo_item;
*/
--  Percentual de um tipo de elemento ao Volume total
select	max((obter_vol_elem_nut_pac(b.nr_seq_pac_elem)/100) * coalesce(sum(coalesce(b.qt_volume,0)),0))
into STRICT	pr_item_solucao_w
from	nut_pac_elemento a,
	nut_pac_elem_mat b,
	nut_elemento c
where	a.nr_seq_nut_pac	= nr_sequencia_p
and	a.nr_sequencia		= b.nr_seq_pac_elem
and	a.nr_seq_elemento	= c.nr_sequencia
and	c.ie_tipo_elemento	= ie_tipo_elemento_p
group by c.ie_tipo_elemento, b.nr_seq_pac_elem;

return	pr_item_solucao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
