CREATE OR REPLACE FUNCTION obter_cond_pag_venc_prot ( cd_convenio_p bigint, ie_tipo_protocolo_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_condicao_pagamento_w	bigint;
cd_convenio_w		integer;
ie_tipo_protocolo_w	smallint;
dt_base_venc_w		timestamp;
cd_estabelecimento_w	integer;

C01 CURSOR FOR
	SELECT	cd_condicao_pagamento
	from	convenio_regra_venc
	where	cd_convenio = cd_convenio_p
	and	coalesce(ie_tipo_protocolo, ie_tipo_protocolo_p) = ie_tipo_protocolo_p
	and	(to_char(coalesce(dt_base_venc_w, clock_timestamp()), 'dd'))::numeric  between coalesce(qt_dia_mes_inicial, 1) and coalesce(qt_dia_mes_final, 31)
	and	coalesce(dt_base_venc_w, clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia, clock_timestamp() - interval '1 days'), 'dd') and fim_dia(coalesce(dt_fim_vigencia, clock_timestamp() + interval '1 days'))
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
	order by	coalesce(ie_tipo_protocolo,0);


BEGIN

dt_base_venc_w	:= clock_timestamp();

select	max(cd_condicao_pagamento)
into STRICT	cd_condicao_pagamento_w
from	convenio
where	cd_convenio = cd_convenio_p;

open C01;
loop
fetch C01 into
	cd_condicao_pagamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_condicao_pagamento_w	:= cd_condicao_pagamento_w;
	end;
end loop;
close C01;

return	cd_condicao_pagamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
