CREATE OR REPLACE FUNCTION obter_cond_prescr_proc_etapa ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


condicao_w	varchar(255)	:= '';

BEGIN

select	max(CASE WHEN a.ie_exame_bloqueado='S' THEN OBTER_DESC_EXPRESSAO(302715)  ELSE CASE WHEN a.ie_exame_bloqueado='R' THEN OBTER_DESC_EXPRESSAO(297642)  ELSE CASE WHEN obter_se_recoleta_proc_etapa(a.nr_prescricao,a.nr_seq_prescricao,a.nr_sequencia)='S' THEN obter_desc_expressao(311524)  ELSE '' END  END  END ) ds_exame_bloqueado
into STRICT	condicao_w
from	prescr_proc_etapa a,
	usuario b
where	a.nm_usuario 		= b.nm_usuario
and	nr_prescricao 		= nr_prescricao_p
and     nr_seq_prescricao 	= nr_seq_prescricao_p
order by	a.nr_sequencia;



return	condicao_w;

end;
