CREATE OR REPLACE FUNCTION obter_cond_venc_lote_prot ( nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE

 
dt_referencia_w			timestamp;
ie_tipo_protocolo_w		smallint;
cd_estabelecimento_w		integer;
cd_retorno_w			bigint;


BEGIN 
 
select	trunc(max(dt_entrega_convenio)), 
	max(ie_tipo_protocolo), 
	max(cd_estabelecimento) 
into STRICT	dt_referencia_w, 
	ie_tipo_protocolo_w, 
	cd_estabelecimento_w 
from	protocolo_convenio 
where	nr_seq_lote_protocolo = nr_seq_lote_p;
 
select	obter_cond_pag_venc_protolo(a.cd_convenio, coalesce(a.ie_tipo_protocolo,ie_tipo_protocolo_w),cd_estabelecimento_w,dt_referencia_w) 
into STRICT	cd_retorno_w 
from	lote_protocolo a 
where	a.nr_sequencia = nr_seq_lote_p;
 
return	cd_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
