CREATE OR REPLACE FUNCTION obter_config_mapa (ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


qt_min_temp_w	bigint;


BEGIN

if (ie_tipo_p = 'T') then
	select	max(qt_min_tempo_atualizacao)
	into STRICT	qt_min_temp_w
	from	config_mapa_quimio;
end if;

