SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_config_pepo_graf (ds_campo_p text) RETURNS varchar AS $body$
DECLARE

  retorno_w varchar(1);
  qt_regra_w bigint;


BEGIN

if (ds_campo_p = 'V') then

	retorno_w := 'S';

	select	count(*)
	into STRICT    qt_regra_w
	from	pepo_configuracao_agente
	where	nm_usuario	= wheb_usuario_pck.get_nm_usuario;

	if (coalesce(qt_regra_w,0) > 0) then

		select	coalesce(max('S'),'N')
		into STRICT	retorno_w
		from	pepo_configuracao_agente
		where	nm_usuario	=	wheb_usuario_pck.get_nm_usuario
		and	ds_campo	=	ds_campo_p
		and	ie_opcao_padrao	=	'S';
	end if;
else
	select	coalesce(max('S'),'N')
	into STRICT	retorno_w
	from	pepo_configuracao_agente
	where	nm_usuario	=	wheb_usuario_pck.get_nm_usuario
	and	ds_campo	=	ds_campo_p
	and	ie_opcao_padrao	=	'S';

end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
