		coalesce(cd_perfil,0);

BEGIN

nm_usuario_ativo_w	:= coalesce(nm_usuario_p, coalesce(obter_usuario_ativo,'AAA'));
cd_perfil_ativo_w	:= coalesce(cd_perfil_p, coalesce(obter_perfil_ativo,0));

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

return	ds_retorno_w;

