SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conpaci_titulo ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_interno_conta_w	bigint;
nr_seq_protocolo_w	bigint;
nr_documento_w		varchar(22);


BEGIN

select	max(a.nr_interno_conta)
into STRICT	nr_interno_conta_w
from	titulo_receber a
where	a.nr_titulo	= nr_titulo_p;

if (coalesce(nr_interno_conta_w::text, '') = '') then

	select	max(a.nr_seq_protocolo),
		to_char(max(a.nr_documento))
	into STRICT	nr_seq_protocolo_w,
		nr_documento_w
	from	titulo_receber a
	where	a.nr_titulo	= nr_titulo_p;

	if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then

		if (coalesce(nr_documento_w::text, '') = '') then

			select	max(a.nr_interno_conta)
			into STRICT	nr_interno_conta_w
			from	conta_paciente a
			where	a.nr_seq_protocolo	= nr_seq_protocolo_w
			and	coalesce(nr_documento_w::text, '') = '';

		else

			select	a.nr_interno_conta
			into STRICT	nr_interno_conta_w
			from	conta_paciente a
			where	a.nr_seq_protocolo	= nr_seq_protocolo_w
			and	exists (SELECT	1
				from	conta_paciente_guia x
				where	x.nr_interno_conta	= a.nr_interno_conta
				and	x.cd_autorizacao	= nr_documento_w);

		end if;

	end if;

end if;

return	nr_interno_conta_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_conpaci_titulo ( nr_titulo_p bigint) FROM PUBLIC;

