CREATE OR REPLACE FUNCTION obter_conselho_medico_format (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(255);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(c.sg_conselho)||max(m.uf_crm)||max('-')||max(m.nr_crm)
	into STRICT	ds_retorno_w
	from	pessoa_fisica p,
		medico m,
		conselho_profissional c
	where	p.cd_pessoa_fisica	= m.cd_pessoa_fisica
	and	p.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	c.nr_sequencia		= p.nr_seq_conselho;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
