CREATE OR REPLACE FUNCTION obter_conselho_profissional ( nr_seq_conselho_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
IE_OPCAO_P:
D - Descricao conselho
S - Sigla conselho
*/
ds_retorno_w	varchar(60);
ds_conselho_w	varchar(60);
sg_conselho_w	varchar(10);


BEGIN
if (nr_seq_conselho_p IS NOT NULL AND nr_seq_conselho_p::text <> '') then
	if (coalesce(ie_opcao_p,'S') = 'S') then		
		select	max(sg_conselho)
		into STRICT	ds_retorno_w
		from	conselho_profissional
		where	nr_sequencia = nr_seq_conselho_p;
		
	elsif (coalesce(ie_opcao_p,'S') = 'D') then	
		select	max(ds_conselho)
		into STRICT	ds_retorno_w
		from	conselho_profissional
		where	nr_sequencia = nr_seq_conselho_p;
	
	elsif (coalesce(ie_opcao_p,'S') = 'P') then	
		select	max(cd_ptu)
		into STRICT	ds_retorno_w
		from	conselho_profissional
		where	nr_sequencia = nr_seq_conselho_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
