SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_consiliacao_cirurgia ( nr_cirurgia_p bigint, cd_material_p bigint, cd_unidade_medida_p text, qt_disp_pepo_p bigint, ie_opcao_p text, nr_seq_lote_fornec_p bigint default 0) RETURNS bigint AS $body$
DECLARE


/*
P - Prescricao
E - Enfermagem dispensou
*/
nr_prescricao_w			bigint;
qt_material_w			double precision;
qt_dispensacao_w		double precision;
qt_retorno_w			double precision;
ie_consolidacao_generico_w	varchar(1);
cd_material_estoque_w		integer;


BEGIN

ie_consolidacao_generico_w := Obter_Param_Usuario(872, 152, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_consolidacao_generico_w);

--if	(nvl(qt_disp_pepo_p,0) > 0) then
--	qt_retorno_w := qt_disp_pepo_p;
--else
	if (ie_opcao_p = 'P') then

		select	nr_prescricao
		into STRICT	nr_prescricao_w
		from	cirurgia
		where	nr_cirurgia	=	nr_cirurgia_p;

		select	sum(coalesce(qt_material,0))
		into STRICT	qt_material_w
		from	prescr_material
		where	nr_prescricao	=	nr_prescricao_w
		and	cd_material	=	cd_material_p
		and	cd_unidade_medida = 	cd_unidade_medida_p;

		qt_retorno_w	:=	qt_material_w;

	elsif (ie_opcao_p = 'E') then
		if (ie_consolidacao_generico_w = 'S') then
			select	sum(coalesce(qt_dispensacao,0))
			into STRICT	qt_dispensacao_w
			from	cirurgia_agente_disp a,
				material b
			where	a.cd_material	= 	b.cd_material_estoque
			and	a.ie_operacao	=	'D'
			and	a.nr_cirurgia	=	nr_cirurgia_p
			and	b.cd_material	=	cd_material_p
			and (coalesce(nr_seq_lote_fornec,coalesce(nr_seq_lote_fornec_p,0)) = coalesce(nr_seq_lote_fornec_p,0))
			and	a.cd_unidade_medida =	cd_unidade_medida_p;
		else
			select	sum(coalesce(qt_dispensacao,0))
			into STRICT	qt_dispensacao_w
			from	cirurgia_agente_disp
			where	nr_cirurgia	=	nr_cirurgia_p
			and	ie_operacao	=	'D'
			and	cd_material	=	cd_material_p
			and (coalesce(nr_seq_lote_fornec,coalesce(nr_seq_lote_fornec_p,0)) = coalesce(nr_seq_lote_fornec_p,0))
			and	cd_unidade_medida =	cd_unidade_medida_p;
		end if;

		qt_retorno_w	:=	qt_dispensacao_w;
	end if;
--end if;
return	coalesce(qt_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_consiliacao_cirurgia ( nr_cirurgia_p bigint, cd_material_p bigint, cd_unidade_medida_p text, qt_disp_pepo_p bigint, ie_opcao_p text, nr_seq_lote_fornec_p bigint default 0) FROM PUBLIC;

