SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_consiliacao_perda_cir ( nr_cirurgia_p bigint, cd_material_p bigint, cd_unidade_medida_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
P - Prescricao
E - Enfermagem dispensou
*/
nr_prescricao_w			bigint;
qt_material_w			double precision;
qt_dispensacao_w		double precision;
qt_retorno_w			double precision;
ie_consolidacao_generico_w	varchar(1);
cd_material_estoque_w		integer;


BEGIN

ie_consolidacao_generico_w := Obter_Param_Usuario(872, 152, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_consolidacao_generico_w);

if (ie_opcao_p = 'E') then
	if (ie_consolidacao_generico_w = 'S') then
		select	sum(coalesce(qt_dispensacao,0))
		into STRICT	qt_dispensacao_w
		from	cirurgia_agente_disp a,
			material b
		where	a.cd_material	= 	b.cd_material_estoque
		and	a.ie_operacao	=	'P'
		and	a.nr_cirurgia	=	nr_cirurgia_p
		and	b.cd_material	=	cd_material_p
		and	a.cd_unidade_medida =	cd_unidade_medida_p;
	else
		select	sum(coalesce(qt_dispensacao,0))
		into STRICT	qt_dispensacao_w
		from	cirurgia_agente_disp
		where	nr_cirurgia	=	nr_cirurgia_p
		and	ie_operacao	=	'P'
		and	cd_material	=	cd_material_p
		and	cd_unidade_medida =	cd_unidade_medida_p;
	end if;

	qt_retorno_w	:=	qt_dispensacao_w;

end if;

return	coalesce(qt_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
