CREATE OR REPLACE FUNCTION obter_consiste_atend_solic_min ( cd_operacao_estoque_p bigint, cd_local_estoque_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
cd_material_w		integer;
cd_operacao_estoque_w	smallint;
ie_consiste_w		varchar(1) := 'N';

c01 CURSOR FOR
SELECT	ie_consiste
from	regra_atend_req_minimo
where	coalesce(cd_operacao_estoque, cd_operacao_estoque_w) = cd_operacao_estoque_w
and	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
and	coalesce(cd_local_estoque, cd_local_estoque_p) = cd_local_estoque_p
and	coalesce(cd_material, cd_material_p) = cd_material_p
order by	coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0),
	coalesce(cd_operacao_estoque,0);


BEGIN
cd_operacao_estoque_w := coalesce(cd_operacao_estoque_p, 0);

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

open c01;
loop
fetch c01 into
	ie_consiste_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return ie_consiste_w;
end;
