CREATE OR REPLACE FUNCTION obter_consiste_disp_material ( cd_material_p bigint, cd_local_estoque_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_consiste_w 			varchar(1) := 'N';
ie_material_disponivel_w		varchar(1);
qt_saldo_disponivel_estoque_w	double precision := 0;
ie_consiste_com_saldo_w		varchar(1);


BEGIN 
 
select	substr(obter_se_material_disponivel(cd_material_p),1,1) ie_disponivel_mercado 
into STRICT	ie_material_disponivel_w
;
 
if (ie_material_disponivel_w IS NOT NULL AND ie_material_disponivel_w::text <> '') and (ie_material_disponivel_w <> 'S') then 
	begin 
 
	if (ie_material_disponivel_w = 'E') then 
		begin 
 
		select	coalesce(max(ie_saldo_disp_mercado),'N') 
		into STRICT	ie_consiste_com_saldo_w 
		from	parametro_compras 
		where	cd_estabelecimento = cd_estabelecimento_p;
 
		if (ie_consiste_com_saldo_w = 'N') then 
			qt_saldo_disponivel_estoque_w := obter_saldo_disp_estoque(cd_estabelecimento_p, cd_material_p, cd_local_estoque_p, clock_timestamp());
 
			if (qt_saldo_disponivel_estoque_w <= 0) then 
				begin 
				ie_consiste_w := 'S';
				end;
			end if;
		else 
			ie_consiste_w	:= 'S';
		end if;
 
		end;
	else 
		ie_consiste_w := 'S';
	end if;
 
	end;
end if;
 
return	ie_consiste_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
