CREATE OR REPLACE FUNCTION obter_consiste_idade_proc ( ds_erro_p text) RETURNS varchar AS $body$
DECLARE


/* A rotina verifica se ao lançar o procedimento gera a inconsistência 403 e se ela estiver com Fecha conta =

'S' vai permitir salvar o procedimento */
ie_consiste_w		varchar(1)	:= 'S';
ie_fecha_conta_w	varchar(1)	:= 'N';

BEGIN

if (length(ds_erro_p)	< 16) then
	for	i in 1..length(ds_erro_p) loop
		if (substr(ds_erro_p,i,3)	= '403') then
			ie_consiste_w	:= 'N';
		end if;
	end loop;

	if (ie_consiste_w	= 'N') then
		select	coalesce(max(ie_fecha_conta),'N')
		into STRICT	ie_fecha_conta_w
		from	inconsistencia
		where	cd_inconsistencia = 403;
	end if;
	if (ie_fecha_conta_w	= 'N') then
		ie_consiste_w	:= 'S';
	end if;
end if;

return	ie_consiste_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
