CREATE OR REPLACE FUNCTION obter_consiste_nf_mesmo_nr ( cd_cgc_p text, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';


BEGIN

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin

	select	coalesce(max(ie_consiste_nr_serie_nf),'S')
	into STRICT	ie_retorno_w
	from	pessoa_juridica_estab
	where	cd_cgc = cd_cgc_p
	and	cd_estabelecimento = cd_estabelecimento_p;

	end;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin

	select	coalesce(max(ie_consiste_nr_serie_nf),'S')
	into STRICT	ie_retorno_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
