CREATE OR REPLACE FUNCTION obter_consistir_se_mae_rn (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

			 
nr_atendimento_rn_w	bigint;
ie_count_rn_w		bigint;
			

BEGIN 
 
	SELECT count(nr_atendimento) 
	into STRICT ie_count_rn_w 
	FROM atendimento_paciente 
	WHERE nr_atendimento_mae = nr_atendimento_p --atendimento da mae 
	AND coalesce(dt_alta::text, '') = '';
	 
	if (ie_count_rn_w > 1) then 
		nr_atendimento_rn_w := -1;
	else 
		SELECT coalesce(max(nr_atendimento),0) 
		into STRICT nr_atendimento_rn_w 
		FROM atendimento_paciente 
		WHERE nr_atendimento_mae = nr_atendimento_p --atendimento da mae 
		AND coalesce(dt_alta::text, '') = '';
	end if;
 
return nr_atendimento_rn_w;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
