CREATE OR REPLACE FUNCTION obter_consulta_soap (nr_atendimento_p bigint, cd_profissional_p bigint) RETURNS bigint AS $body$
DECLARE


    nr_retorno_w 		bigint;


BEGIN

	SELECT 	MAX(a.nr_sequencia)
	INTO STRICT	nr_retorno_w
	FROM	ATENDIMENTO_SOAP a,
			AGENDA_CONSULTA b,
			ATENDIMENTO_PACIENTE c
	WHERE	a.nr_SEQ_AGENDA_consulta = b.nr_sequencia
	AND 	b.nr_Atendimento = c.nr_Atendimento
	AND		a.nr_atendimento = nr_Atendimento_p
	and	c.ie_nivel_atencao = 'P'
	AND		a.cd_profissional = cd_profissional_p
	AND		(c.dt_atend_medico IS NOT NULL AND c.dt_atend_medico::text <> '')
	AND		coalesce(c.dt_fim_consulta::text, '') = '';

return	nr_retorno_w;

end;
