CREATE OR REPLACE FUNCTION obter_consumo_centro_conta ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, dt_inicial_p timestamp, qt_dias_passados_p bigint, cd_conta_contabil_p text, cd_centro_custo_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			double precision;


BEGIN

select	coalesce(sum(qt_consumo),0) qt_movimento
into STRICT	ds_retorno_w
from  	movimento_estoque_v a,
      	material m
where 	a.cd_material_estoque = m.cd_material_estoque
and 	dt_mesano_referencia   = dt_mesano_referencia_p
and 	a.cd_estabelecimento   = cd_estabelecimento_p
and	a.cd_conta_contabil    = cd_conta_contabil_p
and	a.cd_centro_custo      = cd_centro_custo_p
and 	(a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> '')
and	(a.cd_conta_contabil IS NOT NULL AND a.cd_conta_contabil::text <> '')
and 	dt_movimento_estoque   between trunc(dt_inicial_p,'dd') - qt_dias_passados_p and dt_inicial_p;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
