CREATE OR REPLACE FUNCTION obter_consumo_local_periodo ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_material_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
qt_consumo_w		double precision;
qt_dias_w		bigint;
cd_material_estoque_w	integer;


dt_inicial_w		timestamp;
dt_final_w		timestamp;
dt_mesano_inicio_w	timestamp;
dt_mesano_fim_w		timestamp;


BEGIN
dt_mesano_inicio_w	:= trunc(dt_inicial_p,'mm');
dt_mesano_fim_w		:= trunc(dt_final_p,'mm');
qt_dias_w		:= obter_dias_entre_datas(dt_inicial_p, dt_final_p)+1;
dt_inicial_w		:= trunc(dt_inicial_p,'dd');
dt_final_w		:= (trunc(coalesce(dt_final_p, clock_timestamp()),'dd') + 86399/86400);

select	cd_material_estoque
into STRICT	cd_material_estoque_w
from	material
where	cd_material = cd_material_p;

select	coalesce(sum(a.qt_consumo),0)
into STRICT	qt_consumo_w
from	movimento_estoque_v2 a
where	dt_processo between dt_inicial_w and dt_final_w
and	a.cd_material_estoque	= cd_material_estoque_w
and	a.dt_mesano_referencia between dt_mesano_inicio_w and dt_mesano_fim_w
and (a.cd_local_estoque 	= cd_local_estoque_p or coalesce(cd_local_estoque_p,0) = 0)
and	a.cd_estabelecimento	= cd_estabelecimento_p;

if (ie_tipo_p = 'Q') then
	vl_retorno_w := qt_consumo_w;
elsif (ie_tipo_p = 'M') then
	vl_retorno_w := dividir(qt_consumo_w,qt_dias_w);
end if;

return  vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
