SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_consumo_mat_dias (cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



qt_consumo_30_dias_w			double precision;
qt_consumo_90_dias_w			double precision;
qt_consumo_180_dias_w			double precision;
dt_inicio_w				timestamp;
dt_fim_w					timestamp;
ds_retorno_w				varchar(80);



BEGIN

dt_fim_w	:=	PKG_DATE_UTILS.END_OF(clock_timestamp(),'DD', 0);
dt_inicio_w :=  	PKG_DATE_UTILS.START_OF(clock_timestamp() - interval '179 days','DD', 0);

--180 dias
select	dividir(coalesce(sum(qt_consumo),0),90) qt_consumo_ant
into STRICT	qt_consumo_180_dias_w
from   	movimento_estoque_v a,
	material m
where  	a.cd_material_estoque	= m.cd_material_estoque
and  	m.cd_material         	= cd_material_p
and  	a.cd_estabelecimento 	= cd_estabelecimento_p
and  	a.dt_movimento_estoque between dt_inicio_w and dt_fim_w
and 	((not exists (
		SELECT	1
		from	operacao_nota x
		where	x.cd_operacao_estoque = a.cd_operacao_estoque
		and	coalesce(x.ie_transferencia_estab, 'N') = 'S'))
		and (a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> ''));
--180 dias
dt_inicio_w :=  PKG_DATE_UTILS.START_OF(clock_timestamp() - interval '89 days','DD', 0);
-- 90
select	dividir(coalesce(sum(qt_consumo),0),90) qt_consumo_ant
into STRICT	qt_consumo_90_dias_w
from   	movimento_estoque_v a,
	material m
where  	a.cd_material_estoque	= m.cd_material_estoque
and  	m.cd_material         	= cd_material_p
and  	a.cd_estabelecimento 	= cd_estabelecimento_p
and  	a.dt_movimento_estoque between dt_inicio_w and dt_fim_w
and 	((not exists (
		SELECT	1
		from	operacao_nota x
		where	x.cd_operacao_estoque = a.cd_operacao_estoque
		and	coalesce(x.ie_transferencia_estab, 'N') = 'S'))
		and (a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> ''));

--90
dt_inicio_w :=  PKG_DATE_UTILS.START_OF(clock_timestamp() - interval '29 days','DD', 0);
--30
select	dividir(coalesce(sum(qt_consumo),0),30) qt_consumo_ant
into STRICT	qt_consumo_30_dias_w
from   	movimento_estoque_v a,
	material m
where  	a.cd_material_estoque	= m.cd_material_estoque
and  	m.cd_material         	= cd_material_p
and  	a.cd_estabelecimento 	= cd_estabelecimento_p
and  	a.dt_movimento_estoque between dt_inicio_w and dt_fim_w
and 	((not exists (
		SELECT  1
		from	operacao_nota x
		where	x.cd_operacao_estoque = a.cd_operacao_estoque
		and	coalesce(x.ie_transferencia_estab, 'N') = 'S'))
		and (a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> ''));
--30
	if (qt_consumo_180_dias_w <= 0)  then
		ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(314225); --Sem movimentação 180 Dias
	else if (qt_consumo_90_dias_w <= 0)  then
		ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(314228); --Sem movimentação 90 Dias
	else if (qt_consumo_30_dias_w <= 0)  then
		ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(314229); --Sem consumo
	else if ((qt_consumo_90_dias_w*30) <= 10)  then
		ds_retorno_w  := WHEB_MENSAGEM_PCK.get_texto(314230); --Baixa movimentação
	else if ((qt_consumo_90_dias_w*30) <= 120) then
		ds_retorno_w  := WHEB_MENSAGEM_PCK.get_texto(314231); --Normal 1
	else if ((qt_consumo_90_dias_w*30) <= 1200) then
		ds_retorno_w  := WHEB_MENSAGEM_PCK.get_texto(314233); --Normal 2
        else ds_retorno_w := WHEB_MENSAGEM_PCK.get_texto(314234); --Alta Movimentação
       	end if;
	end if;
	end if;
	end if;
	end if;
	end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
