CREATE OR REPLACE FUNCTION obter_consumo_medio_material ( ie_tipo_p text, cd_estabelecimento_p bigint, cd_material_p bigint, dt_mesano_referencia_p timestamp, qt_meses_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			double precision;
qt_consumo_w			double precision;
vl_consumo_w			double precision;
dt_mesano_referencia_w		timestamp;


BEGIN

if (coalesce(dt_mesano_referencia_p::text, '') = '') then
	dt_mesano_referencia_w	:= pkg_date_utils.start_of(clock_timestamp(),'MONTH', 0);
else
	dt_mesano_referencia_w	:= pkg_date_utils.start_of(dt_mesano_referencia_p,'MONTH', 0);
end if;

select	sum(coalesce(a.qt_consumo,0)) qt_estoque,
	sum(coalesce(a.vl_consumo,0)) vl_consumo
into STRICT	qt_consumo_w,
	vl_consumo_w
from	movto_estoque_operacao_v a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.cd_material		= cd_material_p
and	pkg_date_utils.start_of(a.dt_mesano_referencia,'MONTH', 0) between pkg_date_utils.add_month(pkg_date_utils.start_of(dt_mesano_referencia_w,'MONTH', 0), ((qt_meses_p-1)*-1), 0) and pkg_date_utils.start_of(dt_mesano_referencia_w,'MONTH', 0);

qt_consumo_w 	:= dividir(qt_consumo_w,qt_meses_p);
vl_consumo_w	:= dividir(vl_consumo_w,qt_meses_p);

if (ie_tipo_p = 'Q') then
	ds_retorno_w := qt_consumo_w;
elsif (ie_tipo_p = 'V') then
	ds_retorno_w := vl_consumo_w;
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
