SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_contagem_reg_inspecao ( nr_seq_registro_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_contagem_w		bigint;
qt_registro_w			bigint;


BEGIN

nr_seq_contagem_w := 1;

select	count(*)
into STRICT	qt_registro_w
from	inspecao_contagem
where	nr_seq_registro = nr_seq_registro_p;

if (qt_registro_w > 0) then

	/*1ª Contagem*/

	select	count(*)
	into STRICT	qt_registro_w
	from	inspecao_contagem
	where	nr_seq_registro = nr_seq_registro_p
	and	coalesce(dt_liberacao::text, '') = ''
	and	nr_seq_contagem = 1;

	if (qt_registro_w > 0) then
		nr_seq_contagem_w := 1;
	else
		/*2ª Contagem*/

		select	count(*)
		into STRICT	qt_registro_w
		from	inspecao_contagem
		where	nr_seq_registro = nr_seq_registro_p
		and	coalesce(dt_liberacao::text, '') = ''
		and	nr_seq_contagem = 2;

		if (qt_registro_w > 0) then
			nr_seq_contagem_w := 2;
		else
			/*3ª contagem*/

			select	count(*)
			into STRICT	qt_registro_w
			from	inspecao_contagem
			where	nr_seq_registro = nr_seq_registro_p
			and	coalesce(dt_liberacao::text, '') = ''
			and	nr_seq_contagem = 3;


			if (qt_registro_w > 0) then
				nr_seq_contagem_w := 3;
			else
				/*Finalizado*/

				nr_seq_contagem_w := 9;
			end if;
		end if;
	end if;
else
	nr_seq_contagem_w := 1;
end if;

return	nr_seq_contagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
