CREATE OR REPLACE FUNCTION obter_contag_apap ( nr_atendimento_p bigint, campo_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


retorno_w	bigint;
ds_comando_w	varchar(4000);

BEGIN

ds_comando_w	:= substr('select count(*) from w_pep_apap where nr_atendimento = :nr_atendimento_p and '|| campo_p
				||' is not null and nm_usuario = :nm_usuario_p',1,4000);

retorno_w := obter_valor_dinamico_bv(ds_comando_w, 'nr_atendimento_p=' 		|| nr_atendimento_p || ';' ||
				      'nm_usuario_p=' 	|| nm_usuario_p, retorno_w);

return	retorno_w;

end;
