CREATE OR REPLACE FUNCTION obter_contas_contabeis_pf ( cd_pessoa_fisica_p text, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(255);
cd_conta_contabil_w		varchar(20);
ie_tipo_conta_w		varchar(1);

c01 CURSOR FOR
	SELECT	cd_conta_contabil,
		ie_tipo_conta
	from	pessoa_fisica_conta_ctb
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and (coalesce(dt_inicio_vigencia, dt_vigencia_p) <= dt_vigencia_p and coalesce(dt_fim_vigencia, dt_vigencia_p) >= dt_vigencia_p)
	order by 1;


BEGIN

open c01;
	loop
	fetch c01 into
		cd_conta_contabil_w,
		ie_tipo_conta_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		if (coalesce(length(ds_resultado_w),0) < 220) then
			ds_resultado_w := ds_resultado_w || cd_conta_contabil_w ||' (' || ie_tipo_conta_w || ')' || ', ';
		end if;
	end loop;
close c01;

return substr(ds_resultado_w,1,coalesce(length(ds_resultado_w),0) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
