CREATE OR REPLACE FUNCTION obter_contas_log_processamento (nr_interno_conta_p conta_paciente.nr_interno_conta%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w   		varchar(4000);
ds_separador_w  	varchar(2) := ',';
ds_retorno_contas_w  	varchar(4000);

c01 CURSOR FOR
SELECT 	distinct a.nr_interno_conta
from 	conta_paciente a, conta_log_processamento b
where 	a.nr_interno_conta = b.nr_interno_conta
and 	b.nr_interno_conta_base = nr_interno_conta_p
and 	a.ie_status_acerto = 2;
	

BEGIN

open C01;
loop
fetch C01 into
	ds_retorno_contas_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		ds_retorno_w := substr(ds_retorno_w || ds_retorno_contas_w || ds_separador_w, 1, 4000);
	end;
end loop;
close C01;

return	substr(ds_retorno_w, 1, length(ds_retorno_w) -1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
