CREATE OR REPLACE FUNCTION obter_contato_emergencia ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w	varchar(4000);
nm_contato_w	varchar(255);
nr_telefone_w	varchar(255);


BEGIN

if (upper(ie_opcao_p)	= 'NF') then --Nome + Telefone
	nm_contato_w	:= obter_compl_pf(cd_pessoa_fisica_p,20,'N');
	nr_telefone_w	:= obter_compl_pf(cd_pessoa_fisica_p,20,'T');
	ds_retorno_w	:= nm_contato_w;
	if (nr_telefone_w IS NOT NULL AND nr_telefone_w::text <> '') then
	
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
			ds_retorno_w := ds_retorno_w ||' - ';
		end if;
		
		ds_retorno_w	:= ds_retorno_w || nr_telefone_w;
	end if;
	
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
