CREATE OR REPLACE FUNCTION obter_conta_caixa ( nr_seq_caixa_p bigint, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE



cd_conta_contabil_w		varchar(20);
qt_vigencia_w			smallint;


BEGIN

select	coalesce(max(cd_conta_contabil), 'X')
into STRICT	cd_conta_contabil_w
from	caixa_conta_contabil
where	nr_seq_caixa = nr_seq_caixa_p
and	substr(obter_se_conta_vigente(cd_conta_contabil, dt_vigencia_p),1,1) = 'S';

if (cd_conta_contabil_w = 'X') then
	select	max(cd_conta_contabil)
	into STRICT	cd_conta_contabil_w
	from	caixa
	where	nr_sequencia = nr_seq_caixa_p;
end if;

return	cd_conta_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
