CREATE OR REPLACE FUNCTION obter_conta_contabil_idade ( dt_vigencia_p timestamp, qt_dias_conta_p bigint) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w	varchar(20);
qt_dias_conta_w		bigint;

c01 CURSOR FOR
SELECT	cd_conta_contabil
from	ctb_regra_idade_conpaci a
where	qt_dias_conta_w >= qt_dias_min
and	qt_dias_conta_w <= coalesce(qt_dias_max, qt_dias_conta_w)
and	a.dt_inicio_vigencia <= dt_vigencia_p
and	coalesce(a.dt_fim_vigencia, dt_vigencia_p) >= dt_vigencia_p
order by	qt_dias_min;


BEGIN

qt_dias_conta_w	:= qt_dias_conta_p;

open C01;
loop
fetch C01 into
	cd_conta_contabil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_conta_contabil_w	:= cd_conta_contabil_w;
	end;
end loop;
close C01;

return	cd_conta_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
