CREATE OR REPLACE FUNCTION obter_conta_contabil_trib ( cd_empresa_p bigint, cd_tributo_p bigint, cd_cgc_p text, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w		varchar(20);
dt_vigencia_w			timestamp;

c01 CURSOR FOR
SELECT	coalesce(cd_conta_contabil,'0')
from	tributo_conta_contabil
where	cd_tributo 	= cd_tributo_p
and	cd_cgc 		= cd_cgc_p
and	cd_empresa	= cd_empresa_p
and (coalesce(dt_inicio_vigencia, dt_vigencia_w) <= dt_vigencia_w and coalesce(dt_fim_vigencia, dt_vigencia_w) >= dt_vigencia_w)
order by dt_inicio_vigencia;


BEGIN

dt_vigencia_w	:= coalesce(dt_vigencia_p,clock_timestamp());

open c01;
loop
fetch c01 into
	cd_conta_contabil_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	cd_conta_contabil_w	:= cd_conta_contabil_w;
end loop;
close c01;

return	cd_conta_contabil_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
