CREATE OR REPLACE FUNCTION obter_conta_contab_pf_pj_estab ( cd_estabelecimento_p bigint, cd_cgc_p text, cd_pessoa_fisica_p text, ie_tipo_p text, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_empresa_w		smallint;
ds_retorno_w		varchar(200);

c01 CURSOR FOR
SELECT	coalesce(cd_conta_contabil,'0')
from	pessoa_jur_conta_cont
where	cd_empresa		= cd_empresa_w
and	coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
and	cd_cgc			= cd_cgc_p
and	ie_tipo_conta		= ie_tipo_p
and (coalesce(dt_inicio_vigencia, dt_vigencia_p) <= dt_vigencia_p and coalesce(dt_fim_vigencia, dt_vigencia_p) >= dt_vigencia_p)
order by dt_inicio_vigencia;


BEGIN
select	max(cd_empresa)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_p;

if (coalesce(cd_pessoa_fisica_p::text, '') = '') then
	open c01;
	loop
	fetch c01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	end loop;
	close c01;
else
	select	max(cd_conta_contabil)
	into STRICT	ds_retorno_w
	from	pessoa_fisica_conta_ctb
	where	cd_empresa	= cd_empresa_w
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	ie_tipo_conta	= ie_tipo_p;
end if;

if (coalesce(ds_retorno_w, 0) = '0') then
	select	max(cd_conta_contabil)
	into STRICT	ds_retorno_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_p
	and	coalesce(cd_pessoa_fisica_p::text, '') = '';
end if;

return ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
