SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conta_cont_dt_vig (cd_pessoa_p text) RETURNS varchar AS $body$
DECLARE

cd_conta_contabil_w		PESSOA_JURIDICA.cd_conta_contabil%type;


BEGIN

if (cd_pessoa_p IS NOT NULL AND cd_pessoa_p::text <> '') then

select 	max(a.CD_CONTA_CONTABIL)
into STRICT	cd_conta_contabil_w
from	PESSOA_JURIDICA a
where	a.cd_cgc = cd_pessoa_p;


if (coalesce(cd_conta_contabil_w::text, '') = '') then


select 	max(a.cd_conta_contabil)
into STRICT	cd_conta_contabil_w
from   	PESSOA_JUR_CONTA_CONT a
where  	a.nr_sequencia = (SELECT	max(b.nr_sequencia)
			FROM    PESSOA_JUR_CONTA_CONT b
			WHERE   b.cd_cgc = cd_pessoa_p and
			b.dt_inicio_vigencia <= clock_timestamp() and
			fim_dia(b.dt_fim_vigencia) >= clock_timestamp());

end if;


if (coalesce(cd_conta_contabil_w::text, '') = '') then
	select max(a.cd_conta_contabil)
	into STRICT   cd_conta_contabil_w
	from   PESSOA_FISICA_CONTA_CTB a
	where  a.nr_sequencia = (SELECT	max(b.nr_sequencia)
				FROM    PESSOA_FISICA_CONTA_CTB b
				WHERE   b.CD_PESSOA_FISICA = cd_pessoa_p and
				b.dt_inicio_vigencia <= clock_timestamp() and
				fim_dia(b.dt_fim_vigencia) >= clock_timestamp());


end if;

end if;

return	cd_conta_contabil_w;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
