CREATE OR REPLACE FUNCTION obter_conta_empresa_ref (cd_empresa_p bigint, cd_conta_referencia_p text) RETURNS varchar AS $body$
DECLARE

  cd_conta_contabil_w conta_contabil.cd_conta_contabil%type;

BEGIN
  begin
    select  	cd_conta_contabil
	into STRICT		cd_conta_contabil_w
	from 	conta_contabil
	where	cd_empresa = cd_empresa_p
