SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conta_financ_cartao_cr (nr_seq_cartao_p bigint) RETURNS bigint AS $body$
DECLARE


cd_conta_financ_w			bigint := null;
cd_empresa_w				bigint;
cd_estabelecimento_w			bigint;
nr_seq_bandeira_w			bigint;
nr_seq_produto_w			bigint;
IE_TIPO_CARTAO_w			varchar(100);
cd_cgc_w				varchar(14);
nr_seq_grupo_w				bigint;

c01 CURSOR FOR
SELECT	a.cd_conta_financ
from	conta_financeira b,
	conta_financ_regra_cartao a
where	a.cd_conta_financ					= b.cd_conta_financ
and	b.cd_empresa						= cd_empresa_w
and	coalesce(b.cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w
and	coalesce(a.nr_seq_bandeira, nr_seq_bandeira_w)		= nr_seq_bandeira_w
and	coalesce(a.nr_seq_trans_fin_doc::text, '') = ''  -- Edgar 03/11/2008, OS 92431, se tiver este campo informado, será gerada a classificação do cheque no rec de caixa
and	((a.IE_TIPO_CARTAO = 'A') or (a.IE_TIPO_CARTAO = IE_TIPO_CARTAO_w))
and	coalesce(a.nr_seq_produto, coalesce(nr_seq_produto_w,0))		= coalesce(nr_seq_produto_w,0)
order	by coalesce(a.nr_seq_bandeira,0),
	coalesce(a.nr_seq_produto, 0);


BEGIN

begin
select	b.cd_empresa,
	a.cd_estabelecimento,
	a.nr_seq_bandeira,
	a.IE_TIPO_CARTAO,
	a.cd_cgc
into STRICT	cd_empresa_w,
	cd_estabelecimento_w,
	nr_seq_bandeira_w,
	IE_TIPO_CARTAO_w,
	cd_cgc_w
from	estabelecimento b,
	movto_cartao_cr a
where	a.nr_sequencia				= nr_seq_cartao_p
and	a.cd_estabelecimento			= b.cd_estabelecimento;

select	max(nr_seq_produto)
into STRICT	nr_seq_produto_w
from	movto_cartao_cr_classif
where	nr_seq_movto			= nr_seq_cartao_p;

if (coalesce(nr_seq_produto_w::text, '') = '') then
	SELECT * FROM obter_produto_financeiro(cd_estabelecimento_w, null, cd_cgc_w, nr_seq_produto_w, null, null, nr_seq_grupo_w) INTO STRICT nr_seq_produto_w, nr_seq_grupo_w;
end if;
exception
when others then
	cd_empresa_w			:= 0;
	cd_estabelecimento_w		:= 0;
end;

cd_conta_financ_w			:= null;
open c01;
loop
fetch c01 into
	cd_conta_financ_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return	cd_conta_financ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_conta_financ_cartao_cr (nr_seq_cartao_p bigint) FROM PUBLIC;

