CREATE OR REPLACE FUNCTION obter_conta_financ_cheque_cp (nr_seq_cheque_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
	1 - a compensar
	2 - Compensado

*/
cd_conta_financ_w			bigint := null;
cd_empresa_w				bigint;
cd_estabelecimento_w			bigint;
cd_banco_w				cheque.cd_banco%type;
dt_compensacao_w			timestamp;
nr_seq_conta_banco_w			bigint;

c01 CURSOR FOR
SELECT	a.cd_conta_financ
from	conta_financeira b,
	conta_fin_regra_cheque_cp a
where	a.cd_conta_financ					= b.cd_conta_financ
and	b.cd_empresa						= cd_empresa_w
and	coalesce(b.cd_estabelecimento, cd_estabelecimento_w)		= cd_estabelecimento_w
and	coalesce(a.cd_banco, cd_banco_w)				= cd_banco_w
and	coalesce(a.nr_seq_conta_banco,coalesce(nr_seq_conta_banco_w,0))	= coalesce(nr_seq_conta_banco_w,0)
and	(
	 ((coalesce(a.ie_compensar,'N') = 'S') and (ie_opcao_p = '1')) or
	 ((coalesce(a.ie_compensado,'N') = 'S') and (ie_opcao_p = '2'))
	)
order	by coalesce(a.cd_banco,0),
	coalesce(nr_seq_conta_banco,0);


BEGIN

begin
select	b.cd_empresa,
	a.cd_estabelecimento,
	a.cd_banco,
	a.nr_seq_conta_banco
into STRICT	cd_empresa_w,
	cd_estabelecimento_w,
	cd_banco_w,
	nr_seq_conta_banco_w
from	estabelecimento b,
	cheque a
where	a.nr_sequencia			= nr_seq_cheque_p
and	a.cd_estabelecimento		= b.cd_estabelecimento;
exception
when others then
	cd_empresa_w			:= 0;
	cd_estabelecimento_w		:= 0;
end;

cd_conta_financ_w			:= null;
open c01;
loop
fetch c01 into
	cd_conta_financ_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return	cd_conta_financ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
