CREATE OR REPLACE FUNCTION obter_conta_financ_conv_receb (cd_estabelecimento_p bigint, cd_convenio_p bigint) RETURNS bigint AS $body$
DECLARE


cd_conta_financ_w	bigint;
cd_cgc_w		varchar(14);


BEGIN

select	CD_CGC /*aamfirmo 17/01/2013 OS  536096 -  nao passava o CD_CGC para obter_conta_financeira e nunca buscava o tipo de pessoa desta PJ.*/
into STRICT 	cd_cgc_w
