CREATE OR REPLACE FUNCTION obter_conta_financ_param ( cd_estabelecimento_p bigint, ie_tipo_saldo_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_tipo_saldo_p
	1 - Saldo anterior
	2 - Saldo atual
*/
cd_conta_financ_w CONTA_FINANCEIRA.CD_CONTA_FINANC%type;


BEGIN


if (ie_tipo_saldo_p = 1) then

select	cd_conta_financ_sant
into STRICT	cd_conta_financ_w
from	parametro_fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_p;

elsif (ie_tipo_saldo_p = 2) then

select	cd_conta_financ_saldo
into STRICT	cd_conta_financ_w
from	parametro_fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_p;

end if;

return	cd_conta_financ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
