CREATE OR REPLACE FUNCTION obter_conta_financ_protocolo (ie_entrada_saida_p text, cd_estabelecimento_p bigint, cd_convenio_p bigint) RETURNS bigint AS $body$
DECLARE


cd_conta_financ_w			conta_financeira.cd_conta_financ%type;
nr_seq_conta_financ_w		conta_financeira.cd_conta_financ%type;


C01 CURSOR FOR
	SELECT	a.nr_seq_conta_financ
	from	conta_financeira b,
			conta_financ_regra a
	where	a.nr_seq_conta_financ								= b.cd_conta_financ
	and		coalesce(a.cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p
	and		coalesce(a.cd_convenio, coalesce(cd_convenio_p,0))			= coalesce(cd_convenio_p,0)
	and		a.ie_entrada_saida									= ie_entrada_saida_p
	and	b.ie_situacao											= 'A'
	order by
		coalesce(a.cd_convenio, 0);


BEGIN

open C01;
loop
fetch C01 into
	cd_conta_financ_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		nr_seq_conta_financ_w:= cd_conta_financ_w;
	end;
end loop;
close C01;

return	nr_seq_conta_financ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
