CREATE OR REPLACE FUNCTION obter_conta_hist_oper_estoque ( cd_empresa_p bigint, cd_operacao_estoque_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w	varchar(20);
cd_historico_w		bigint;
ds_retorno_w		varchar(20);
/*
c - conta contabil
h - histórico padrão
*/
c01 CURSOR FOR
SELECT	cd_conta_contabil,
	cd_historico
from	operacao_estoque_contab
where	cd_empresa		= cd_empresa_p
and	cd_operacao_estoque	= cd_operacao_estoque_p
and (coalesce(dt_inicio_vigencia, clock_timestamp()) <= clock_timestamp() and coalesce(dt_fim_vigencia, clock_timestamp()) >= clock_timestamp())
order by dt_inicio_vigencia;


BEGIN

open c01;
loop
fetch c01 into
	cd_conta_contabil_w,
	cd_historico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	cd_conta_contabil_w	:= cd_conta_contabil_w;
	cd_historico_w		:= cd_historico_w;
end loop;
close c01;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_conta_contabil_w;
elsif (ie_opcao_p = 'H') then
	ds_retorno_w	:= substr(cd_historico_w,1,10);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
