CREATE OR REPLACE FUNCTION obter_conta_operacao_terceiro ( nr_seq_terceiro_p bigint, dt_operacao_p timestamp, nr_seq_operacao_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_conta_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_conta_w
from (
	SELECT	b.nr_sequencia
	from	terceiro_conta b
	where	coalesce(nr_seq_operacao_p,0)	= 0
	and	b.nr_seq_terceiro		= nr_seq_terceiro_p
	and	b.ie_status_conta		= 'P'
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	trunc(b.dt_mesano_referencia,'month') = trunc(dt_operacao_p,'month')
	
union

	SELECT	b.nr_sequencia
	from	operacao_terceiro a,
		terceiro_conta b
	where	coalesce(nr_seq_operacao_p,0)	<> 0
	and	b.nr_seq_terceiro		= nr_seq_terceiro_p
	and	b.ie_status_conta		= 'P'
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	trunc(b.dt_mesano_referencia,'month') = trunc(dt_operacao_p,'month')
	and	a.nr_sequencia		= nr_seq_operacao_p
	and	coalesce(b.cd_condicao_pagamento,0) = coalesce(a.cd_condicao_pagamento,coalesce(b.cd_condicao_pagamento,0))
	) alias10;

return	nr_seq_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
