CREATE OR REPLACE FUNCTION obter_conta_outro_retornos ( nr_seq_retorno_p bigint, nr_interno_conta_p bigint, cd_autorizacao_p text) RETURNS bigint AS $body$
DECLARE


ie_retorno_w	bigint;


BEGIN

select	count(*)
into STRICT	ie_retorno_w
from (	SELECT   1
	from	convenio_retorno b,
		convenio_retorno_item a
	where	a.nr_interno_conta   = nr_interno_conta_p
	--and	a.cd_autorizacao     = cd_autorizacao_p
	and	a.nr_seq_retorno     = b.nr_sequencia
	and	b.nr_sequencia       <> nr_seq_retorno_p
	
union

	SELECT   1
	from    lote_audit_hist_guia a,
		lote_audit_hist b
	where   a.nr_seq_lote_hist  = b.nr_sequencia
	and     coalesce(b.dt_fechamento::text, '') = ''
	and     a.nr_interno_conta   = nr_interno_conta_p
	and     a.cd_autorizacao     = cd_autorizacao_p) alias2;

return 	ie_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
