SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conta_paciente_etapa (nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_etapa_w		varchar(80)	:= null;
nr_seq_etapa_w		bigint	:= null;
ds_retorno_w		varchar(255)	:= null;
ds_observacao_w		varchar(255);
ie_muda_status_conta_w	varchar(01);
ie_insere_protocolo_w	varchar(1);
dt_etapa_w		timestamp;
nm_usuario_w		varchar(15);
ie_desdobrar_planserv_w	fatur_etapa.ie_desdobrar_planserv%type;

c01 CURSOR FOR
	SELECT	a.nr_seq_etapa,
		b.ds_etapa,
		substr(a.ds_observacao,1,255),
		coalesce(ie_muda_status_conta, 'S'),
		coalesce(ie_insere_protocolo, 'S'),
		coalesce(a.nm_usuario_nrec, a.nm_usuario),
		a.dt_etapa,
		coalesce(ie_desdobrar_planserv, 'S')
	from	fatur_etapa b,
		conta_paciente_etapa a
	where	a.nr_seq_etapa	= b.nr_sequencia
	and	a.nr_interno_conta	= nr_interno_conta_p
	and	coalesce(a.dt_fim_etapa::text, '') = ''
	and	coalesce(b.ie_situacao,'A')= 'A'
	order by a.dt_etapa;


/*
D - Decrição Etapa
U - Usuário
O - Observação da etapa
T - Data da etapa
E - Permite desdobrar planserv
*/
BEGIN

open	c01;
loop
fetch	c01 into
	nr_seq_etapa_w,
	ds_etapa_w,
	ds_observacao_w,
	ie_muda_status_conta_w,
	ie_insere_protocolo_w,
	nm_usuario_w,
	dt_etapa_w,
	ie_desdobrar_planserv_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= nr_seq_etapa_w;
	elsif (ie_opcao_p = 'O') then
		ds_retorno_w	:= ds_observacao_w;
	elsif (ie_opcao_p = 'S') then
		ds_retorno_w	:= ie_muda_status_conta_w;
	elsif (ie_opcao_p = 'P') then
		ds_retorno_w	:= ie_insere_protocolo_w;
	elsif (ie_opcao_p = 'U') then
		ds_retorno_w	:= nm_usuario_w;
	elsif (ie_opcao_p = 'T') then
		ds_retorno_w	:= dt_etapa_w;
	elsif (ie_opcao_p = 'E') then
		ds_retorno_w	:= ie_desdobrar_planserv_w;
	else
		ds_retorno_w	:= ds_etapa_w;
	end if;

	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
