CREATE OR REPLACE FUNCTION obter_conta_padrao (cd_cgc_p text) RETURNS varchar AS $body$
DECLARE

nr_conta_w		pessoa_juridica_conta.nr_conta%type;


BEGIN
	if (coalesce(cd_cgc_p,0) <> 0) then
		SELECT MAX(nr_conta)
		into STRICT nr_conta_w
		FROM pessoa_juridica_conta
		WHERE ie_conta_pagamento = 'S'
		AND cd_cgc = cd_cgc_p;
	end if;

	if (coalesce(nr_conta_w,0) <> 0) then
		SELECT MAX(nr_conta)
		into STRICT nr_conta_w
		FROM pessoa_juridica_conta
		WHERE cd_cgc = cd_cgc_p;
	end if;
return	nr_conta_w;

end;
$body$
LANGUAGE PLPGSQL
