CREATE OR REPLACE FUNCTION obter_conta_partic_ret_glosa (nr_seq_ret_glosa_p bigint, nr_seq_Propaci_p bigint, nr_seq_matpaci_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_interno_conta_w	bigint;
ds_retorno_w		varchar(200);


BEGIN
if (nr_seq_Propaci_p IS NOT NULL AND nr_seq_Propaci_p::text <> '')then

	select max(nr_interno_conta)
	into STRICT	nr_interno_conta_w
	from	procedimento_paciente
	where 	nr_sequencia = nr_seq_Propaci_p;

else

	select max(nr_interno_conta)
	into STRICT	nr_interno_conta_w
	from	material_atend_paciente
	where 	nr_sequencia = nr_seq_matpaci_p;


end if;

if (ie_opcao_p ='A') then

	ds_retorno_w	:= nr_interno_conta_w;

end if;

return ds_retorno_w;

end;
