CREATE OR REPLACE FUNCTION obter_conta_titulo (nr_interno_Conta_p conta_paciente.nr_interno_Conta%type, nr_titulo_p titulo_receber.nr_titulo%type) RETURNS bigint AS $body$
DECLARE


ds_valor_invalido_w	varchar(20) := obter_desc_expressao(621074); --Valor invalido
nr_sequencia_w 		conta_paciente.nr_interno_conta%type;


BEGIN
	if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '' AND nr_interno_conta_p > 0) then
		return nr_interno_Conta_p;
	end if;
	
	if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
		select max(nr_interno_conta)
		into STRICT   nr_sequencia_w
		from   titulo_receber 
		where  nr_titulo = nr_titulo_p;
	end if;
	
	return nr_sequencia_w;
exception
  when others then
    return(ds_valor_invalido_w);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
