CREATE OR REPLACE FUNCTION obter_conta_tit_rec_dist (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	bigint;


BEGIN

select	max(coalesce(a.nr_seq_conta_banco_deb_aut,a.nr_seq_conta_banco))
into STRICT	nr_retorno_w
from	pls_contrato_pagador_fin a,
	pls_contrato_pagador b,
	pls_mensalidade c,
	titulo_receber d
where	b.nr_sequencia		= a.nr_seq_pagador
and	b.ie_tipo_pagador	= 'P'
and	c.nr_seq_pagador	= b.nr_sequencia
and	d.nr_seq_mensalidade	= c.nr_sequencia
and	d.nr_titulo		= nr_titulo_p;


if (coalesce(nr_retorno_w::text, '') = '') then

	select	max(coalesce(a.nr_seq_conta_banco_deb_aut,a.nr_seq_conta_banco))
	into STRICT	nr_retorno_w
	from	pls_contrato_pagador_fin a,
		pls_mensalidade c,
		titulo_receber d
	where	c.nr_seq_pagador_fin	= a.nr_sequencia
	and	d.nr_seq_mensalidade	= c.nr_sequencia
	and	d.nr_titulo		= nr_titulo_p;

end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_conta_tit_rec_dist (nr_titulo_p bigint) FROM PUBLIC;

