CREATE OR REPLACE FUNCTION obter_conta_transf_est_pj ( cd_empresa_p bigint, cd_estabelecimento_p bigint, cd_estab_destino_p bigint, cd_cgc_p text, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w	varchar(40);
cd_cgc_w		varchar(14);

c01 CURSOR FOR
SELECT	coalesce(cd_conta_contabil,'0')
from	pessoa_jur_conta_cont
where	cd_empresa		= cd_empresa_p
and	cd_cgc			= cd_cgc_p
and	ie_tipo_conta		= 'T'
and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
and	cd_estab_destino	= cd_estab_destino_p
and (coalesce(dt_inicio_vigencia, dt_vigencia_p) <= dt_vigencia_p and coalesce(dt_fim_vigencia, dt_vigencia_p) >= dt_vigencia_p)
order by 	coalesce(cd_estabelecimento,0),
		dt_inicio_vigencia;



BEGIN

open c01;
loop
fetch c01 into
	cd_conta_contabil_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return cd_conta_contabil_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
