CREATE OR REPLACE FUNCTION obter_conteudo_email_cme (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cme_w		varchar(2000);
ds_material_w		varchar(254);
qt_material_w		double precision;
ds_observacao_w		varchar(255);

c01 CURSOR FOR
	SELECT	SUBSTR(cme_Obter_Nome_Conjunto(a.NR_SEQ_CONJUNTO),1,125) ds_material,
		a.qt_conjunto,
		SUBSTR(a.ds_observacao,1,255)
	FROM	agenda_pac_cme a
	WHERE	a.nr_seq_agenda = nr_seq_agenda_p
	AND	a.ie_origem_inf	= 'I'
	ORDER BY  cme_Obter_Nome_Conjunto(a.NR_SEQ_CONJUNTO) DESC;


BEGIN

OPEN c01;
LOOP
FETCH c01 INTO
	ds_material_w,
	qt_material_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	BEGIN
	ds_cme_w	:=  substr(ds_material_w || ' (Qt: ' || qt_material_w || ')' || CHR(13) || CHR(10) || ds_observacao_w || CHR(13) || CHR(10) || ds_cme_w,1,2000);
	END;
END LOOP;
CLOSE c01;

ds_cme_w	:= substr('CME: ' || CHR(13) || CHR(10) || ds_cme_w,1,2000);

RETURN 	SUBSTR(ds_cme_w,1,2000);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
