SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conteudo_email_equip (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_equip_w		varchar(2000);

/* Vari√°veis de Equipamentos */

ds_classificacao_w	varchar(60);

c01 CURSOR FOR
	SELECT	substr(obter_desc_classif_equip(nr_seq_classif_equip),1,60)
	from	agenda_pac_equip a
	where	a.nr_seq_agenda = nr_seq_agenda_p
	and	coalesce(a.ie_origem_inf,'I') = 'I'
	and     (nr_seq_classif_equip IS NOT NULL AND nr_seq_classif_equip::text <> '')
	
union

	SELECT	substr(OBTER_DESC_EQUIP_AGENDA(a.cd_equipamento),1,60)
	from	agenda_pac_equip a
	where	a.nr_seq_agenda = nr_seq_agenda_p
	and	coalesce(a.ie_origem_inf,'I') = 'I'
	and     (cd_equipamento IS NOT NULL AND cd_equipamento::text <> '')
	;


BEGIN

open c01;
loop
fetch c01 into
	ds_classificacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_equip_w	:= ds_classificacao_w || ', ' || ds_equip_w;
end loop;
close c01;

ds_equip_w	:= Wheb_mensagem_pck.get_texto(308874) || ': ' /*'Equipamentos: '*/ || ds_equip_w;
ds_equip_w	:= substr(ds_equip_w,1,length(ds_equip_w) -2) || chr(13) || chr(10);

return 	substr(ds_equip_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_conteudo_email_equip (nr_seq_agenda_p bigint) FROM PUBLIC;

