SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_conteudo_email_macro_ag (nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		 varchar(4000) := '';
/*
U = ds_orientacao_usuario
P = ds_orientacao_pac
*/
nr_seq_proc_interno_w   		agenda_paciente.nr_seq_proc_interno%type;
ds_orientacao_usuario_w   		proc_interno.ds_orientacao_usuario%type;
ds_orientacao_pac_w   			proc_interno.ds_orientacao_pac%type;


BEGIN
select 	max(nr_seq_proc_interno)
into STRICT 	nr_seq_proc_interno_w
from 	agenda_paciente
where 	nr_sequencia = nr_seq_agenda_p;


select 	max(ds_orientacao_usuario),
		max(ds_orientacao_pac)
into STRICT	ds_orientacao_usuario_w,
		ds_orientacao_pac_w
from	proc_interno
where	nr_sequencia = nr_seq_proc_interno_w;

if (coalesce(ie_opcao_p,'N') = 'U') then
	ds_retorno_w := ds_orientacao_usuario_w;
elsif (coalesce(ie_opcao_p,'N') = 'P') then
	ds_retorno_w := ds_orientacao_pac_w;
end if;

return 	substr(ds_retorno_w,1,4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
